	  		and S_NAME = #{sName,jdbcType=VARCHAR}


	  		and S_PROVINCE = #{sProvince,jdbcType=VARCHAR}


	  		and S_CITY = #{sCity,jdbcType=VARCHAR}

   		order by S_SORT DESC


